(function() {
  var $, Template, jqueryify;

  $ = require('jquery');

  jqueryify = '../dt-jquery'.jqueryify;

  Template = require('dynamictemplate').Template;

  module.exports = {
    simple: function(æ) {
      var tpl;
      tpl = jqueryify(new Template({
        schema: 5
      }, function() {
        return this.$div({
          "class": 'content'
        }, function() {
          return this.$p("bla");
        });
      }));
      return tpl.ready(function() {
        console.log(tpl.jquery);
        æ.equal("<div class=\"content\"><p>bla</p></div>", tpl.jquery.outerHTML());
        return æ.done();
      });
    }
  };

}).call(this);
