(function() {
  var BufferStream, render, streamify;

  BufferStream = require('bufferstream');

  streamify = require('dt-stream');

  render = function(template) {
    var buffer;
    buffer = new BufferStream({
      encoding: 'utf-8',
      size: 'flexible',
      disabled: true
    });
    streamify(template).stream.pipe(buffer);
    return buffer;
  };

  module.exports = render;

}).call(this);
